def longest_common_subset_length(arr: list) -> int:
    """"""
    n = len(arr)
    length = 0
    lps = [0] * n
    i = 1
    while i < n:
        if arr[i] == arr[length]:
            length += 1
            lps[i] = length
            i += 1
        elif length != 0:
            length = lps[length - 1]
        else:
            lps[i] = 0
            i += 1
    return max(lps)
