def count_strings_with_suffix_array(n, k, suffix_array):
    MOD = 998244354
    pos = [0] * (n + 1)
    for i in range(n):
        pos[suffix_array[i]] = i
    pos[n] = -1
    cnt = 0
    for i in range(n - 1):
        if pos[suffix_array[i] + 1] > pos[suffix_array[i + 1] + 1]:
            cnt += 1
    (num, denom) = (1, 1)
    for i in range(n):
        num = num * (k - cnt + n - 1 - i) % MOD
        denom = denom * (i + 1) % MOD
    return num * pow(denom, MOD - 2, MOD) % MOD

