import math

def find_minimum_m(test_cases):
    results = []
    for case in test_cases:
        (k, arr) = case
        arr.sort()
        if k % 2 != 0:
            results.append(0)
            continue
        m = -1
        for i in range(k):
            if arr[i] % 2 == 0:
                m = 1
                break
        if m == -1:
            for i in range(k):
                if (arr[i] - 1) // 2 % 2 == 0 and arr[i] > 2:
                    m = 2
                    break
        if m == -1:
            M = []
            for i in range(k):
                if math.log(arr[i] + 1, 2) != int(math.log(arr[i] + 1, 2)) and arr[i] > 2:
                    b = list(bin(arr[i])[2:])
                    if '0' in b:
                        M.append(pow(2, b[::-1].index('0')))
            if M:
                m = min(M)
        results.append(m)
    return results

