def is_perfectly_balanced(s: str) -> bool:
    """"""
    unique_chars = set()
    for i in range(len(s)):
        c = s[i]
        if c in unique_chars:
            if c != s[0]:
                return False
            for j in range(i + 1, len(s)):
                if s[j] != s[j - i]:
                    return False
            return True
        unique_chars.add(c)
    return True
