    for i in range(n):
        hsh[A[i]] -= 1
    cnt = 0
    ans = float('inf')
    for i in hsh:
        if hsh[i] >= 2:
            cnt += 1
    if len(set(A)) == n:
        return 0
    for i in range(n):
        hsh2 = defaultdict(int)
        ct = 0
        for j in range(i, n):
            hsh2[A[j]] += 1
            if hsh2[A[j]] == hsh[A[j]] - 1 and hsh[A[j]] >= 2:
                ct += 1
            if ct == cnt:
                ans = min(ans, abs(j - i + 1))
    return ans

