def find_xy_with_close_digit_sums(n: int) -> tuple:
    """"""
    s = ''
    s1 = ''
    c = 1
    while n > 0:
        d = n % 10
        if d % 2 == 0:
            s = str(d // 2) + s
            s1 = str(d // 2) + s1
        elif c == 1:
            s = str(d // 2) + s
            s1 = str(d // 2 + 1) + s1
            c -= 1
        else:
            s = str(d // 2 + 1) + s
            s1 = str(d // 2) + s1
            c += 1
        n = n // 10
    return (int(s), int(s1))
