def count_painting_ways(N, S1, S2):
    mod = 10 ** 9 - 7
    l = ''
    for i in range(N):
        if S1[i] == S2[i]:
            l += 'X'
        else:
            l += 'Y'
    l = l.replace('YY', 'Y')
    ans = 6 if l[0] == 'Y' else 3
    for i in range(1, len(l)):
        if l[i] == 'Y' and l[i - 1] == 'Y':
            ans *= 3
        elif l[i] == 'X' and l[i - 1] == 'X':
            ans *= 2
        elif l[i] == 'Y' and l[i - 1] == 'X':
            ans *= 2
        ans = ans % mod
    return ans

