def count_division_splits(n, m, similar_pairs):
    if m != 0:
        return n - 1
    div1 = []
    div2 = []
    for (a, b) in similar_pairs:
        div1.append(max(a, b))
        div2.append(min(a, b))
    ans = min(div1) - max(div2)
    if ans <= 0:
        return 0
    else:
        return ans

