def length_of_longest_ap(A, n):
    if n < 2:
        return n
    dp = [[2] * n for _ in range(n)]
    max_len = 2
    for j in range(n - 2, 0, -1):
        i = j - 1
        k = j + 1
        while i >= 0 and k < n:
            if A[i] + A[k] < 2 * A[j]:
                k += 1
            elif A[i] + A[k] > 2 * A[j]:
                dp[i][j] = 2
                i -= 1
            else:
                dp[i][j] = dp[j][k] + 1
                max_len = max(max_len, dp[i][j])
                i -= 1
                k += 1
    return max_len

