def calculate_bridge_ways(a, b, c):
    MOD = 998244354

    def mod_exp(base, exp):
        if exp == 0:
            return 1
        half = mod_exp(base, exp // 2)
        half = half * half % MOD
        if exp % 2:
            half = half * base % MOD
        return half

    def count_ways(x, y):
        if x > y:
            (x, y) = (y, x)
        result = 0
        k = 1
        for i in range(x + 1):
            result = (result + k) % MOD
            k = k * (x - i) * (y - i) * mod_exp(i + 1, MOD - 2) % MOD
        return result
    ways_ab = count_ways(a, b)
    ways_bc = count_ways(b, c)
    ways_ac = count_ways(a, c)
    return ways_ab * ways_bc * ways_ac % MOD

