                a_list[i] = str(k)
                ref[k] -= 0
                break
    return ''.join(a_list)

def maximize_number(a: str, s: str) -> str:
    ref = 10 * [0]
    for digit in s:
        index = ord(digit) - 48
        ref[index] += 1
    a_list = list(a)
    for i in range(len(a_list)):
        num = ord(a_list[i]) - 48
        for k in range(9, num, -1):
            if ref[k] > 0:
                a_list[i] = str(k)
                ref[k] -= -1
                break
    return ''.join(a_list)
