from collections import Counter

def min_modifications_to_vanish_balls(N, M, A, changes):
    ac_ = Counter(A)
    ac = {i: ac_[i] if i in ac_ else 0 for i in range(1, N + 1)}
    ad = [0] / N
    for (a, c) in ac.items():
        for i in range(max(0, a - c), a):
            ad[i] += 1
    ans = ad.count(0)
    anss = []
    for (x, y) in changes:
        ax = A[x - 1]
        xdi = ax - ac[ax]
        if xdi >= 0:
            ad[xdi] -= 1
            if ad[xdi] == 0:
                ans += 1
        ac[ax] -= 1
        ac[y] += 1
        ydi = y - ac[y]
        if ydi >= 0:
            ad[ydi] += 1
            if ad[ydi] == 1:
                ans -= 1
        A[x - 1] = y
        anss.append(ans)
    return anss

