def count_valid_scenarios(n, k, questions):
    ans = 3 * n - 2
    a = set()
    b = set()
    for val in questions:
        if val - 1 in a:
            b.add((val, val - 1))
        if val + 1 in a:
            b.add((val, val + 1))
        a.add(val)
    ans += len(a) + len(b)
    return max(ans, 0)

