def calculate_max_candies(N, candies):

    def divisors(N):
        for i in range(1, int(N ** 0.5) + 1):
            if N % i == 0:
                yield i
            if N / i != i and N % (N / i) == 0 and (i != 1):
                yield (N // i)
    T = candies[:]
    for i in range(2, N):
        maxm = T[i]
        for j in divisors(i + 1):
            maxm = max(maxm, T[i] + T[j - 1])
        T[i] = maxm
    return T

