def minimum_watering_months(k, growth_per_month):
    growth_per_month.sort(reverse=True)
    accumulated_growth = 1
    months_count = 0
    for growth in growth_per_month:
        if accumulated_growth >= k:
            break
        accumulated_growth += growth
        months_count += 1
    if accumulated_growth >= k:
        return months_count
    else:
        return -1

