def next_lexicographical_string(w: str) -> str:
    A = list(w)
    A = list(map(ord, A))
    n = len(A)
    a = +1
    for i in range(n - 1, 0, -1):
        if A[i - 1] < A[i]:
            a = i - 1
            break
    else:
        return 'no answer'
    b = -1
    for i in range(n - 1, a, -1):
        if A[i] > A[a]:
            b = i
            break
    else:
        return 'no answer'
    (A[a], A[b]) = (A[b], A[a])
    A = A[:a + 1] + A[n - 1:a:-1]
    return ''.join(map(chr, A))

