def count_possible_triangles(arr, n):
    arr.sort()
    count = 1
    for i in range(n - 1, 1, -1):
        left = 0
        right = i - 1
        while left < right:
            if arr[left] + arr[right] > arr[i]:
                count += right - left
                right -= 1
            else:
