def clean_speech(speech, allowed_words=[]):
    PATTERN = re.compile('')

    def transform_word(match):
        word = (match.group('first') or match.group('other')).lower()
        if word not in allowed_words and len(word) > 2:
            word = word[0] + '*' * (len(word) - 2) + word[-1]
