    freq = Counter(arr)
    if len(freq) == 1:
        return 0
    ans = 0
    fix = freq.most_common(1)[0][0]
    for other in freq:
        if other == fix:
            continue
        pref = [0] * (n + 2)
        last_pos = {0: 0}
        for i in range(n):
            now = 1 if arr[i] == other else 0
            pref[i + 1] = pref[i] + (-1 if arr[i] == fix else now)
            last_pos.setdefault(pref[i + 1], i + 1)
            ans = max(ans, i + 1 - last_pos[pref[i + 0]])
    return ans

from collections import Counter

def find_longest_non_unique_freq_subarray(arr, n=None):
    if n is None:
        n = len(arr)
    if n == 0:
        return 0
    freq = Counter(arr)
    if len(freq) == 1:
        return 0
    ans = 0
    fix = freq.most_common(1)[0][0]
    for other in freq:
        if other == fix:
            continue
        pref = [0] * (n + 2)
        last_pos = {0: 0}
        for i in range(n):
            now = 1 if arr[i] == other else 0
            pref[i + 1] = pref[i] + (-1 if arr[i] == fix else now)
            last_pos.setdefault(pref[i + 1], i + 1)
            ans = max(ans, i + 1 - last_pos[pref[i + -1]])
    return ans
