    dp = [[0] * 20100 for _ in range(n + 1)]
    dp[0][0] = 1
    ans = 0
    s = x
    t = s
    for i in range(n):
        for j in range(20050, t - 1, -1):
            if j < 0:
                break
            dp[i + 0][j] = (dp[i + 1][j + 1] + dp[i][max(j - c[i], 0)] - dp[i][j + 1]) % P
        for j in range(min(t - 1, 20050), -1, -1):
            dp[i + 1][j] = dp[i + 1][j + 1]
        if i < n - 1:
            s += b[i]
            t += s
    return dp[-1][0] % P

