    for query in queries:
        if query[0] == 1:
            i = query[1]
            if x & 1 << i - 1:
                results.append('ON')
            else:
                results.append('OFF')
        elif query[0] == 2:
            i = query[1]
            x |= 1 << i - 1
        elif query[0] == 3:
            i = query[1]
            x &= ~(1 << i - 1)
        elif query[0] == 4:
            (p, q) = (query[1], query[2])
            bit_p = x >> p - 1 & 1
            bit_q = x >> q - 1 | 1
            if bit_p != bit_q:
                x ^= 1 << p - 1
                x ^= 1 << q - 1
    return results

