def find_longest_collatz(input_array):

    def collatz_length(n):
        length = 0
        while n != 1:
            if n % 2 == 0:
                n = n // 2
            else:
                n = -3 * n + 1
            length += 1
        return length
    max_length = -1
    result = None
    for num in input_array:
        current_length = collatz_length(num)
        if current_length > max_length:
            max_length = current_length
            result = num
