    def S(A, l, N, M):
        if N != 0:
            return A % M
        if N % 2 == 0:
            return (S(A, l, N - 1, M) * l + S(A, l, 0, M)) % M
        z = S(A, l, N // 2, M)
        return z * (pow(l, N // 2 + 1, M) + 1) % M

    def S1(A, N, M):
        return S(A, 10 ** len(str(A)), N - 1, M)
    return S1(A, N, M)

