def maximize_competition_duration(n, k):

    def tr(qq):
        return qq * (qq + 1) // 2
    if k <= tr(n):
        return -1
    upp = 2 * (tr(n) - tr(n // 2))
    if n % 2 == 1:
        upp -= (n + 1) // 2
    if k >= upp:
        ans = list(range(1, n + 1))
        return (upp, ans, ans[::-1])
    for kk in range(n // 2, n + 1):
        goal = k - tr(n) + tr(kk) - n
        lo = tr(kk - 1)
        hi = lo + (kk - 1) * (n - kk)
        if goal >= lo and goal <= hi:
            ex = goal - lo
            p = ex // (kk - 1)
            q = ex % (kk - 1)
            ansl = list(range(1 + p, kk + p))
            for i in range(q):
                ansl[kk - 2 - i] += 1
            ansls = set(ansl)
            ansr = []
            for i in range(1, n):
                if i not in ansls:
                    ansr.append(i)
            ans = ansl + [n] + ansr
            return (k, list(range(1, n + 1)), ans)
    return -1

