def merge_sorted_arrays(arr1, arr2):
    """"""
    merged_array = []
    (p1, p2) = (0, 0)
    (N, M) = (len(arr1), len(arr2))
    while p1 < N and p2 < M:
        if arr1[p1] >= arr2[p2]:
            merged_array.append(arr1[p1])
            p1 += 1
        else:
            merged_array.append(arr2[p2])
            p2 += 1
    while p1 < N:
        merged_array.append(arr1[p1])
        p1 += 1
    while p2 < M:
        merged_array.append(arr2[p2])
        p2 += 1
    return merged_array
