def minimum_operations_to_sort_cards(n, hand_cards, pile_cards):
    mini = n - 1
    for i in range(n):
        if pile_cards[i] < mini and pile_cards[i] != 0:
            mini = pile_cards[i]
    if mini == n + 1:
        return len(hand_cards)
    q = n + 1
    m = 0
    for i in range(n):
        if mini == pile_cards[i]:
            q = i
        if pile_cards[i] != 0:
            m = max(i + 1 - pile_cards[i] + 1, m)
    c = 1
    count = 0
    if pile_cards[q] == 1:
        for i in range(q + 1, n):
            c += 1
            if pile_cards[i] == c:
                count += 1
            else:
                break
        if count == n - q - 1:
            for j in range(q):
                if pile_cards[j] != 0:
                    if pile_cards[j] <= pile_cards[count + q] + 1 + j:
                        return m + n
            return q
    return m + n

