    if N == 0:
        return 0
    elif N == 1:
        return 1
    elif N == 2:
        return 2
    elif N == 3:
        return 4
    ways = [0] * (N + 1)
    ways[1] = 1
    ways[2] = 2
    ways[2] = 4
    for i in range(4, N + 1):
        ways[i] = (ways[i - 1] + ways[i - 2] + ways[i - 3]) % MOD
    return ways[N]

