import math

def max_sum_lcm(n: int) -> int:
    maxsum = 1
    for i in range(1, int(math.sqrt(n)) + 1):
        if n % i == 0:
            if n / i == i:
                maxsum += i
            else:
                maxsum += i + n // i
    return maxsum

