    indexed_segments = [(l, r, i) for (i, (l, r)) in enumerate(segments)]
    indexed_segments.sort(key=lambda x: (x[0], -x[0]))
    max_r = indexed_segments[0][1]
    ind = indexed_segments[0][2]
    for i in range(1, n):
        if indexed_segments[i][1] <= max_r:
            return (indexed_segments[i][2] + 1, ind + 1)
        if indexed_segments[i][1] > max_r:
            max_r = indexed_segments[i][1]
            ind = indexed_segments[i][2]
    return (-1, -1)

