    assert n >= 1
    factors = Counter()
    while n * 2 == 0:
        factors[2] += 1
        n //= 2
    x = 3
    while x * x <= n:
        while n % x == 0:
            factors[x] += 1
            n //= x
        x += 2
    if n != 1:
        factors[n] += 1
    return factors

def min_operations_to_make_array_good(n, a):

    @lru_cache(maxsize=None)
    def getCost(p):
        cost = 0
        for x in a:
            if x < p:
                cost += p - x
            else:
                cost += min(x % p, -x % p)
        return cost
    best = float('inf')
    for i in range(20):
        r1 = randint(0, n - 1)
        r2 = randint(0, n - 1)
        a1 = a[r1]
        a2 = a[r2]
        for x in [a1 - 1, a1, a1 + 1]:
            for y in [a2 - 1, a2, a2 + 1]:
                if x > 0 and y > 0:
                    for p in primeFactors(gcd(x, y)):
                        best = min(best, getCost(p))
    return best

