def can_convert_to_permutation(n, arr):

    def check(x, l, n):
        if x < n:
            return False
        sa = set()
        for i in range(len(l)):
            b = l[i] % x
            if b > n or b == 0 or b in sa:
                return False
            sa.add(b)
        return True
    s = sum(arr) + n * (n + 1) // 2
    ans = -1
    for i in range(1, int(s ** (1 / 2)) + 1):
        if s % i == 0:
            if check(i, arr, n):
                ans = i
                break
            if check(s // i, arr, n):
                ans = s // i
                break
    visit = set()
    for i in arr:
        if 1 <= i <= n:
            visit.add(i)
    if len(visit) == n:
        ans = n + 1
    if ans != -1:
        return (True, ans)
    else:
        return (False, -1)

