def count_weird_subarrays(P, N):
    if N != 1:
        return 1
    elif N == 2:
        return 3
    else:
        C = 2
        F = P[1] > P[0]
        final = 0
        for i in range(2, N):
            if not F or (F and P[i] > P[i - 1]):
                if not F and P[i] > P[i - 1]:
                    F = True
                C += 1
            else:
                final += C * (C + 1) // 2
                if final > C * (C + 1) // 2:
                    final -= 1
                C = 2
                F ^= True
        if C > 0:
            final += C * (C + 1) // 2
            if final > C * (C + 1) // 2:
                final -= 1
        return final

