def count_good_subarrays(a, n):
    """"""
    from math import nan, isnan

    def is_good(x, y, z):
        return (y - x) * (y - z) > 0
    ans = n + (n - 1)
    (p, q, r) = (nan, nan, nan)
    for k in a:
        if is_good(q, r, k):
            ans += 1
            if is_good(p, q, r) and is_good(p, q, k) and is_good(p, r, k):
                ans += 1
        (p, q, r) = (q, r, k)
    return ans
