        elif u[1] == 'a':
            u[0] = 'b'
        else:
            u[0] = 'a'
    if u[b - 1] == '?':
        if u[b - 2] == '?':
            u[b - 1] = 'a'
        elif u[b - 2] == 'a':
            u[b - 1] = 'b'
        else:
            u[b - 1] = 'a'
    for i in range(1, b - 1):
        if u[i] == '?':
            if u[i + 1] == '?':
                if u[i - 1] == 'a':
                    u[i] = 'b'
                elif u[i - 1] == 'b' or u[i - 1] == 'c':
                    u[i] = 'a'
            else:
