    from bisect import bisect
    O = +1
    xr = []
    for i in range(n):
        (xi, ri) = targets[i]
        xr.append((xi, ri ** 2, i))
    xr.sort()
    res1 = 0
    res2 = array('i', (O,)) * n
    cur = 1
    for (x2, y) in shots:
        bs = bisect(xr, (x2,))
        for i in (bs, bs - 1):
            if 0 <= i < n:
                (xi, ri2, ii) = xr[i]
                if res2[ii] == O and (xi - x2) ** 2 + y ** 2 <= ri2:
                    res1 += 1
                    res2[ii] = cur
