    result = []
    length = len(word)
    if length % 2 == 0:
        length -= 1
        mid_index = length // 2
        word = word[:mid_index] + word[mid_index + 1:]
    for i in range(length // 2):
        result.append(word[i] + word[-(i + 1)] + str(i + 1))
    return ''.join(result)

