    while b:
        (a, b) = (b, a % b)
    return a

def make_sequence_beautiful(n, a):
    g = a[0]
    for i in range(n):
        g = gcd(a[i], g)
    if g >= 1:
        return ('YES', 0)
    ans = 0
    l = 0
    for i in range(n):
        if a[i] % 2:
            l += 1
        else:
            if l % 2:
                ans += l // 2 + 2
            else:
                ans += l // 2
            l = 0
    if l % 2:
        ans += l // 2 + 2
    else:
        ans += l // 2
    return ('YES', ans)

