def calculate_cake_layer_sum(test_cases, cake_layers_list):
    results = []
    for i in range(test_cases):
        n = len(cake_layers_list[i])
        sum1 = 1
        final_sum = 0
        num = cake_layers_list[i]
        rep_time = factorial(n - 1)
        rep_count = {}
        for layer in num:
            if layer in rep_count:
                rep_count[layer] += 1
            else:
                rep_count[layer] = 1
        for layer in rep_count:
            if rep_count[layer] == 1:
                sum1 += layer * factorial(n - rep_count[layer])
            else:
                sum1 += layer * factorial(n - 1) / factorial(n - rep_count[layer])
        for k in range(n):
            final_sum += sum1 * 10 ** k
        results.append(int(final_sum))
    return results

