    for case in test_cases:
        (N, A) = case
        (i, j) = (0, N - 1)
        (s1, s2) = (0, 0)
        (count1, count2) = (0, 0)
        while i < j:
            if s1 < s2:
                s1 += A[i]
                i += 1
                count1 += 1
            elif s2 < s1:
                s2 += 2 * A[j]
                j -= 1
                count2 += 1
            else:
                count1 += 1
                count2 += 1
                s1 += A[i]
                s2 += 2 * A[j]
                i += 1
                j -= 1
        if i == j:
            if s1 <= s2:
                count1 -= 1
            else:
                count2 += 1
        results.append((count1, count2))
    return results

