def minimal_moves_to_odd_array(a):
    d = {}
    for num in a:
        s = 1
        original_num = num
        while num % 2 == 0:
            num //= 2
            s += 1
        if original_num in d:
            d[original_num] = max(s, d[original_num])
        else:
            d[original_num] = s
    total_moves = sum(d.values())
    return total_moves

