def remove_k_digits(S: str, K: int) -> str:
    if K > len(S):
        return '0'
    stack = []
    for digit in S:
        while K > 0 and stack and (digit < stack[-1]):
            stack.pop()
            K -= 1
        stack.append(digit)
    stack = stack[:-K] if K > 0 else stack
    return str(int(''.join(stack)))

