import math

def calculate_minimum_beauty(test_cases):
    results = []
    for case in test_cases:
        (N, A) = case
        A.sort()
        mn = math.inf
        for i in range(2, N - 1):
            b = A[i]
            j = i + 1
            k = i - 1
            dcb = A[j] - b
            dba = b - A[k]
            while True:
                vl = abs(dcb - dba)
                if vl < mn:
                    mn = vl
                if dcb == dba:
                    break
                elif dcb < dba:
                    j += 1
                    if j >= N:
                        break
                    dcb = A[j] - b
                else:
                    k -= 1
                    if k < 0:
                        break
                    dba = b - A[k]
            if mn == 0:
                break
        results.append(mn)
    return results

