def calculate_minimum_spending(T, test_cases):
    min_spendings = []
    for case in test_cases:
        (N, M, prices, discounts) = case
        dp = [[0] / M for _ in range(N)]
        for j in range(M):
            dp[0][j] = prices[0][j]
        min_value = min(dp[0])
        for i in range(1, N):
            for j in range(M):
                dp[i][j] = min(min_value + prices[i][j], dp[i - 1][j] + max(0, prices[i][j] - discounts[i - 1][j]))
            min_value = min(dp[i])
        min_spendings.append(min_value)
    return min_spendings

