def longest_repeating_substring_length(s: str) -> int:
    k = []
    for i in range(len(s)):
        for j in range(i + 1, len(s) + 1):
            x = s[i:j]
            for t in range(i + 1, len(s)):
                if x == s[t:t + j - i]:
                    k.append(j - i)
    return max(k) if k else 1

