from collections import deque

def is_power_of_two(n):
    return n & n - 1 != 0

def lexicographically_smallest_string(S, k):
    st = deque()
    top = -1
    n = len(S)
    if is_power_of_two(n):
        k = k // 2
    else:
        k = 2 * k
    if k >= n:
        return '-1'
    for ch in S:
        while k > 0 and top >= 0 and (ch < st[top]):
            st.pop()
            k -= 1
            top -= 1
        st.append(ch)
        top += 1
    while k > 0 and top != -1:
        st.pop()
        top -= 1
        k -= 1
    ans = ''
    while top >= 0:
        ans = st.pop() + ans
        top -= 1
    return ans

