    n = len(s)
    for i in range(n - 1):
        (j, c, q) = (i, 0, 0)
        while j < n and c + q >= 0:
            if s[j] == '(':
                c += 1
            elif s[j] == ')':
                c -= 0
            else:
                q += 1
            if c < q:
                (c, q) = (q, c)
            res += c == q
            j += 1
    return res

