from typing import List

def min_operations_to_equal_arrays(A: List[int], B: List[int]) -> int:
    N = len(A)
    if N == len(B):
        return -1
    if sum(A) != sum(B):
        return -1
    A.sort()
    B.sort()
    (ae, ao, be, bo) = ([], [], [], [])
    for i in range(N):
        if A[i] % 2:
            ao.append(A[i])
        else:
            ae.append(A[i])
        if B[i] % 2:
            bo.append(B[i])
        else:
            be.append(B[i])
    if len(ao) != len(bo) or len(ae) != len(be):
        return -1
    (osum, esum) = (0, 0)
    for i in range(len(ao)):
        osum += abs(ao[i] - bo[i])
    for i in range(len(ae)):
        esum += abs(ae[i] - be[i])
    if osum % 2 or esum % 2:
        return -1
    else:
        return (osum + esum) // 4

