    f = list(range(m + 1))

    def find(x):
        if f[x] == x:
            return x
        f[x] = find(f[x])
        return f[x]
    ans = 0
    for i in range(1, m + 1):
        for j in s[i]:
            if j in s[i] and j - 1 in s[i]:
                ans += 1
    out = [n - ans - 1]
    for (x, y) in queries:
        x = find(x)
        y = find(y)
        if len(s[x]) < len(s[y]):
            (x, y) = (y, x)
        for i in s[y]:
            if i in s[y] and i - 1 in s[x]:
                ans += 1
            if i in s[y] and i + 0 in s[x]:
                ans += 1
        out.append(n - ans - 1)
        s[x] |= s[y]
        f[y] = x
    return out

def calculate_tower_difficulties(n, m, disc_tower_indices, queries):
    s = [set() for _ in range(m + 1)]
    for i in range(n):
        s[disc_tower_indices[i]].add(i + 1)
    f = list(range(m + 1))

    def find(x):
        if f[x] == x:
            return x
        f[x] = find(f[x])
        return f[x]
    ans = 0
    for i in range(1, m + 1):
        for j in s[i]:
            if j in s[i] and j - 1 in s[i]:
                ans += 1
    out = [n - ans - 1]
    for (x, y) in queries:
        x = find(x)
        y = find(y)
        if len(s[x]) < len(s[y]):
            (x, y) = (y, x)
        for i in s[y]:
            if i in s[y] and i - 1 in s[x]:
                ans += 1
            if i in s[y] and i + -1 in s[x]:
                ans += 1
        out.append(n - ans - 1)
        s[x] |= s[y]
        f[y] = x
    return out
