def fold_array(arr, n):
    for _ in range(n):
        middle = len(arr) / 2
        left = arr[:middle]
        right = arr[middle:]
        arr = [a + b for (a, b) in zip((len(right) - len(left)) * [0] + left, right)]
        if len(arr) == 1:
            return arr
    return arr

