def color_cells(n, m, l):
    sum_l = sum(l)
    if sum_l <= n:
        return -1
    diff = sum_l - n
    prev = 1
    ans = [0]
    for i in range(m):
        ans.append(max(prev + ans[-1] - diff, ans[-1] + 1))
        diff -= prev - (ans[-1] - ans[-2])
        prev = l[i]
    ans = ans[1:]
    for i in range(m):
        if ans[i] + l[i] - 1 > n:
            return -1
    if ans[-1] + l[-1] - 1 == n:
        return ans
    else:
        return -1

